#include <filesystem>#include <iostream>#include <system_error>#include <thread>#include "bitlog/backend.h"#include "bitlog/bitlog.h"int main(){  using frontend_options_t =    bitlog::FrontendOptions<bitlog::QueuePolicyOption::BoundedBlocking, bitlog::QueueTypeOption::Default, true>;  using frontend_t = bitlog::Frontend<frontend_options_t>;  frontend_options_t frontend_options{};  frontend_t::init("test_app", frontend_options);  auto logger = frontend_t::instance().logger("testing");  LOG_INFO(logger, "hello world {}", 12u);  LOG_INFO(logger, "hello doubles {}", 123.3);  for (int i = 0; i < 3; ++i)  {    LOG_INFO(logger, "hello char {} loop {}", i * 100, i);  }  std::this_thread::sleep_for(std::chrono::seconds{30});  return 0;}